import React, { useState } from 'react';

const RegistrationPage = () => {
    const [name, setName] = useState('');
    const [expertise, setExpertise] = useState('');
    const [education, setEducation] = useState('');
    const [experience, setExperience] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = {
            name,
            expertise,
            education,
            experience,
        };
        console.log('Registration data', formData);
        // Add your registration logic here
    };

    return (
        <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
                <h2 className="text-2xl font-semibold text-gray-800 mb-6 text-center">Candidate Registration</h2>
                <form onSubmit={handleSubmit}>
                    {/* Name Field */}
                    <div className="mb-4">
                        <label htmlFor="name" className="block text-gray-700 text-sm font-bold mb-2">
                            Name
                        </label>
                        <input
                            type="text"
                            id="name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder="Enter your name"
                        />
                    </div>

                    {/* Area of Expertise */}
                    <div className="mb-4">
                        <label htmlFor="expertise" className="block text-gray-700 text-sm font-bold mb-2">
                            Area of Expertise (keywords or tags)
                        </label>
                        <input
                            type="text"
                            id="expertise"
                            value={expertise}
                            onChange={(e) => setExpertise(e.target.value)}
                            required
                            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder="e.g. Web Development, Data Science"
                        />
                    </div>

                    {/* Education */}
                    <div className="mb-4">
                        <label htmlFor="education" className="block text-gray-700 text-sm font-bold mb-2">
                            Education
                        </label>
                        <input
                            type="text"
                            id="education"
                            value={education}
                            onChange={(e) => setEducation(e.target.value)}
                            required
                            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder="Enter your highest qualification"
                        />
                    </div>

                    {/* Experience */}
                    <div className="mb-6">
                        <label htmlFor="experience" className="block text-gray-700 text-sm font-bold mb-2">
                            Experience (years)
                        </label>
                        <input
                            type="number"
                            id="experience"
                            value={experience}
                            onChange={(e) => setExperience(e.target.value)}
                            required
                            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder="Enter your years of experience"
                        />
                    </div>

                    {/* Submit Button */}
                    <div className="flex items-center justify-between">
                        <button
                            type="submit"
                            className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                            Register
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default RegistrationPage;
