import React, { useState } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';
import BGIMAGE from '../BAckground.jpeg'

const AdminLoginPage = () => {
    const [username, setUsername] = useState('');
    const [pin, setPin] = useState('');
    const [errors, setErrors] = useState({});
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    // Input validation function
    const validateForm = () => {
        const newErrors = {};

        // Validate username: not empty
        if (!username.trim()) {
            newErrors.username = 'Username is required';
        }

        // Validate pin: must be 6 digits
        if (!pin.trim()) {
            newErrors.pin = 'Pin is required';
        } else if (pin.length !== 6 || isNaN(pin)) {
            newErrors.pin = 'Pin must be a 6-digit number';
        }

        return newErrors;
    };

    // Submit handler
    const handleSubmit = (e) => {
        e.preventDefault();

        // Perform validation
        const validationErrors = validateForm();

        if (Object.keys(validationErrors).length === 0) {
            // If no validation errors, submit form data
            axios.post('http://localhost:8081/admin-login', {
                username,
                pin
            })
                .then(response => {
                    if (response.data.success) {
                        setIsLoggedIn(true);  // Mark admin as logged in
                    } else {
                        setErrors({ login: 'Invalid username or pin' });
                    }
                })
                .catch(error => {
                    console.error(error);
                    setErrors({ login: 'An error occurred during login' });
                });
        } else {
            // Set validation errors if form is invalid
            setErrors(validationErrors);
        }
    };

    if (isLoggedIn) {
        return <Navigate to="/admin-dashboard" />;  // Redirect on successful login
    }

    return (
        <div className="min-h-screen flex flex-col items-center justify-center bg-local">
            <img src={BGIMAGE} className=''/>
            <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
                <h2 className="text-2xl font-semibold text-gray-800 mb-6 text-center">Admin Login</h2>

                <form onSubmit={handleSubmit}>
                    {/* Username Field */}
                    <div className="mb-4">
                        <label htmlFor="username" className="block text-gray-700 text-sm font-bold mb-2">
                            Username
                        </label>
                        <input
                            type="text"
                            id="username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.username ? 'border-red-500' : ''}`}
                            placeholder="Enter your username"
                        />
                        {errors.username && <p className="text-red-500 text-sm mt-1">{errors.username}</p>}
                    </div>

                    {/* Pin Field */}
                    <div className="mb-6">
                        <label htmlFor="pin" className="block text-gray-700 text-sm font-bold mb-2">
                            Pin (6 digits)
                        </label>
                        <input
                            type="password"
                            id="pin"
                            value={pin}
                            onChange={(e) => setPin(e.target.value)}
                            className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.pin ? 'border-red-500' : ''}`}
                            placeholder="Enter your 6-digit pin"
                        />
                        {errors.pin && <p className="text-red-500 text-sm mt-1">{errors.pin}</p>}
                    </div>

                    {/* Submit Button */}
                    <div className="flex items-center justify-between">
                        <button
                            type="submit"
                            className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                            Login
                        </button>
                    </div>

                    {/* Error Message for Invalid Login */}
                    {errors.login && <p className="text-red-500 text-sm mt-4 text-center">{errors.login}</p>}
                </form>
            </div>
        </div>
    );
};

export default AdminLoginPage;
